
class Score extends React.Component {
  constructor(props) {
    // must call super 1st before calling this
    // super calls the parent constructor react.component
    super(props);
    this.state = { score: 0 };

    // make 'this' available in click handlers
    this.incrementScore = this.incrementScore.bind(this);
    this.decrementScore = this.decrementScore.bind(this);
  }

  incrementScore() {
    this.setState({
      score: this.state.score + 1
    });
  }

  decrementScore() {
    this.setState({
      score: this.state.score - 1
    });
  }

  render() {
    return React.createElement(
      "div",
      null,
      React.createElement(
        "h2",
        null,
        "Score board for the ",
        this.props.teamName
      ),
      React.createElement(
        "div",
        null,
        "Score: ",
        this.state.score,
        " ",
        React.createElement("br", null),
        React.createElement(
          "button",
          { onClick: this.incrementScore },
          "+"
        ),
        React.createElement(
          "button",
          { onClick: this.decrementScore },
          "-"
        )
      )
    );
  }
}

var el = document.getElementById('two');
ReactDOM.render(React.createElement(Score, { teamName: "Tigers" }), el);
